// Get webhook data
const webhookRaw = $('Webhook').first().json;
const webhookData = webhookRaw.body || webhookRaw;

// Handle Airtable data structure
// Sometimes it's flat, sometimes it's in .fields
const rec = $json || {};
const settings = rec.fields || rec;

const slug = settings.active_event_slug || null;
const pnr = webhookData.personnummer || null;

const tag = (webhookData.tag || '')
  .trim()
  .toLowerCase();

const name = webhookData.namn || webhookData.name || null;
const telefon = webhookData.telefon || webhookData.telephone || null;
const email = webhookData.email || null;

// Configurable requirements - default OFF (only enabled when explicitly true)
const require_payment = settings.require_payment === true;
const require_membership = settings.require_membership === true;
const require_startgg = settings.require_startgg === true;

// Payment settings for calculating payment_expected
const swish_expected_per_game = settings.swish_expected_per_game || 25;

return [
  {
    json: {
      slug,
      personnummer: pnr,
      tag,
      name,
      telefon,
      email,
      _runEbas: !!pnr,
      _runStartgg: !!tag && !!slug,
      require_payment,
      require_membership,
      require_startgg,
      swish_expected_per_game,
    },
  },
];

// Get Parse Input data
const parseInputItems = $('Parse Input').all();
const ctx = parseInputItems.length > 0 ? parseInputItems[0].json : {};

// Get eBas result
let ebas = { isMember: false };
try {
  const ebasItems = $('eBas Check').all();
  if (ebasItems.length > 0 && ebasItems[0].json && !ebasItems[0].json.error) {
    ebas = ebasItems[0].json;
  }
} catch (e) { /* treat as not member */ }

// Get Start.gg result
let sgg = { isRegistered: false, events: [] };
try {
  const sggItems = $('Start.gg Check').all();
  if (sggItems.length > 0 && sggItems[0].json && !sggItems[0].json.error) {
    sgg = sggItems[0].json;
  }
} catch (e) { /* treat as not registered */ }

// Generate UUID
const uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
  const r = Math.random() * 16 | 0;
  return (c === 'x' ? r : (r & 0x3 | 0x8)).toString(16);
});

// Actual status from external checks
const isActualMember = ebas.isMember === true;
const isActualStartgg = sgg.isRegistered === true;

// Check passed (for status calculation - passes if not required OR confirmed)
const memberCheckPassed = isActualMember || !ctx._runEbas;
const startggCheckPassed = isActualStartgg || !ctx._runStartgg;

// Requirements from settings
const requirePayment = ctx.require_payment;
const requireMembership = ctx.require_membership;
const requireStartgg = ctx.require_startgg;

// Status logic
const paymentOk = !requirePayment;
const memberOk = !requireMembership || memberCheckPassed;
const startggOk = !requireStartgg || startggCheckPassed;
const isReady = paymentOk && memberOk && startggOk;
const status = isReady ? 'Ready' : 'Pending';

// Missing requirements
const missing = [];
if (requirePayment) missing.push('Payment');
if (requireMembership && !memberCheckPassed) missing.push('Membership');
if (requireStartgg && !startggCheckPassed) missing.push('Start.gg');

const events = sgg.events || [];

// Calculate payment_expected based on number of games
const perGame = ctx.swish_expected_per_game || 25;
const payment_expected = events.length * perGame;

return [{ json: {
  uuid, status, payment_valid: false, missing,
  member: isActualMember, startgg: isActualStartgg, is_guest: !isActualStartgg,
  events, name: ctx.name, tag: ctx.tag, slug: ctx.slug,
  telefon: ctx.telefon, email: ctx.email, payment_expected
}}];

if ($json.already_checked_in) {
  return [{ json: $json }];
}
try {
  const results = $('Merge Results').first().json;
  return [{ json: results }];
} catch (e) {
  return [{ json: $json }];
}

const existing = $json.records[0].fields;
const ctx = $('Parse Input').first().json;

return [{
  json: {
    already_checked_in: true,
    status: existing.status || 'Pending',
    payment_valid: existing.payment_valid || false,
    member: existing.member || false,
    startgg: existing.startgg || false,
    name: existing.name || ctx.name,
    tag: existing.tag || ctx.tag,
    message: 'Already checked in'
  }
}];

const records = $json.records || [];
const savedRecordId = $('Save to Airtable').first().json.id;
const savedData = $('Save to Airtable').first().json.fields || $('Merge Results').first().json || {};

if (records.length > 1) {
  const toDelete = records.filter(r => r.id !== savedRecordId).map(r => r.id);
  return [{ json: { ...savedData, _duplicateIds: toDelete } }];
}
return [{ json: { ...savedData, _duplicateIds: [] } }];

