const body = $json.body || $json;
const tag = (body.tag || '').trim();
const slug = body.tournament_slug || body.slug || '';

if (!tag || !slug) {
  return [{ json: { isRegistered: false, error: true, message: !tag ? 'Tag saknas' : 'Slug saknas' } }];
}

return [{ json: { tag, slug } }];

