const data = $json.data;
const tournament = data?.tournament;

if (!tournament) {
  return [{ json: { isRegistered: false, error: true, message: 'Turnering hittades inte' } }];
}

const participants = tournament.participants?.nodes || [];
if (participants.length === 0) {
  return [{ json: { isRegistered: false, tournament: tournament.name, message: 'Ej registrerad' } }];
}

const player = participants[0];
const events = (player.events || []).map(e => e.name);

return [{ json: {
  isRegistered: true,
  tournament: tournament.name,
  tag: player.gamerTag,
  name: player.user?.name || null,
  events: events,
  eventCount: events.length
} }];
